package com.example.test_jstl;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "CustomerManager", value = "/customerManager")
public class CustomerManager extends HttpServlet {

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        List<Customer> customers = new ArrayList<>();
        customers.add(new Customer("Nguyễn Đức Tuyên","01/03/2000","Hà Nội","data:image/jpeg;base64,"));
        customers.add(new Customer("Nguyễn Đức Tuấn","10/03/2000","Hà Nội","data:image/jpeg;base64,"));
        customers.add(new Customer("Nguyễn Đức Tuân","11/05/2000","Hà Nội","data:image/jpeg;base64,"));
        customers.add(new Customer("Nguyễn Đức Tài","01/03/2000","Hà Nội","data:image/jpeg;base64,"));

        request.setAttribute("customers",customers);
        RequestDispatcher rq = getServletContext().getRequestDispatcher("/showCustomer.jsp");
        rq.forward(request,response);
    }
}